package com.bukowiecki.regdebug.parsers.gdb

import org.assertj.core.api.Assertions
import org.junit.Test

/**
 * @author Marcin Bukowiecki
 */
class GDBRegistersParserTest {

    @Test
    fun testParseGroups_1() {
        val given = """
             Group      Type      
             sse        user      
             mmx        user      
             general    user      
             float      user      
             all        user      
             save       internal  
             restore    internal  
             vector     user      
             system     user   
        """.trimIndent()

        val result = GDBRegistersParser.parseGroups(given)

        Assertions
            .assertThat(result)
            .isEqualTo(listOf("sse", "mmx", "general", "float", "all", "save", "restore", "vector", "system"))
    }

